mapper.update:
UPDATE 
	medical_supplies 
SET 
	stock = stock - #{quantity}
	, in_charge = #{in_charge}
	, change_date = default 
WHERE 
	name = #{name} 
AND 
	specifications = #{specifications}
AND 
	visible = 'Y'

mapper.insert:
INSERT INTO
	released
	(
	department
	, name
	, specifications
	, quantity
	, change_date
	, memo
	)
VALUES
	(
	#{department}
	, #{name}
	, #{specifications}
	, #{quantity}
	, default
	, #{memo}
	)

mapper.update:
UPDATE
	medical_supplies
SET
	stock = stock + #{quantity}
	, in_charge = #{in_charge}
	, change_date = default 
WHERE 
	name = #{name} 
AND 
	specifications = #{specifications}
AND 
	visible = 'Y'
